import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import BarberSelection from "./pages/BarberSelection";
import BarberProfile from "./pages/BarberProfile";
import AppointmentScheduler from "./pages/AppointmentScheduler";
import PaymentPage from "./pages/PaymentPage";
import DashboardBarber from "./pages/DashboardBarber";
import DashboardAdmin from "./pages/DashboardAdmin";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";

function App() {
  return (
    <Router>
      <Navbar />
      <div className="min-h-screen p-4">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/barbers" element={<BarberSelection />} />
          <Route path="/barber/:id" element={<BarberProfile />} />
          <Route path="/schedule/:id" element={<AppointmentScheduler />} />
          <Route path="/payment" element={<PaymentPage />} />
          <Route path="/barber-dashboard" element={<DashboardBarber />} />
          <Route path="/admin-dashboard" element={<DashboardAdmin />} />
        </Routes>
      </div>
      <Footer />
    </Router>
  );
}

export default App;
