import React from "react";
import { Link } from "react-router-dom";

export interface Barber {
  id: number;
  name: string;
  specialty: string;
  rating: number;
  image?: string;
}

interface BarberCardProps {
  barber: Barber;
}

export default function BarberCard({ barber }: BarberCardProps) {
  return (
    <div className="border rounded-lg shadow p-4 hover:shadow-lg transition">
      <img
        src={barber.image || "https://via.placeholder.com/150"}
        alt={barber.name}
        className="w-full h-40 object-cover rounded-md mb-3"
      />
      <h3 className="text-lg font-bold">{barber.name}</h3>
      <p className="text-sm text-gray-600">{barber.specialty}</p>
      <p className="text-yellow-500">‚≠ê {barber.rating}</p>
      <Link to={`/barber/${barber.id}`}>
        <button className="mt-3 bg-blue-500 text-white px-4 py-2 rounded">
          View Profile
        </button>
      </Link>
    </div>
  );
}
