import React from 'react';
import { useTheme } from '../contexts/ThemeContext';
import { useBooking } from '../contexts/BookingContext';

export default function BookingSummary() {
  const { colors } = useTheme();
  const { bookingData } = useBooking();

  if (bookingData.selectedServices.length === 0) {
    return null;
  }

  return (
    <div style={{
      backgroundColor: colors.surface,
      border: `1px solid ${colors.border}`,
      borderRadius: '12px',
      padding: '20px',
      marginTop: '20px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
    }}>
      <h3 style={{
        margin: '0 0 16px 0',
        fontSize: '1.25rem',
        fontWeight: '600',
        color: colors.textHeading,
        fontFamily: '"Montserrat", sans-serif'
      }}>
        Booking Summary
      </h3>

      {/* Selected Services */}
      <div style={{ marginBottom: '16px' }}>
        {bookingData.selectedServices.map((service, index) => (
          <div key={service.id} style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '8px 0',
            borderBottom: index < bookingData.selectedServices.length - 1 ? `1px solid ${colors.border}` : 'none'
          }}>
            <div>
              <span style={{
                fontSize: '14px',
                fontWeight: '500',
                color: colors.textHeading,
                fontFamily: '"Montserrat", sans-serif'
              }}>
                {service.name}
              </span>
              {service.quantity > 1 && (
                <span style={{
                  fontSize: '12px',
                  color: colors.textBody,
                  marginLeft: '8px',
                  fontFamily: '"Lora", serif'
                }}>
                  x{service.quantity}
                </span>
              )}
            </div>
            <span style={{
              fontSize: '14px',
              fontWeight: '600',
              color: colors.textHeading,
              fontFamily: '"Montserrat", sans-serif'
            }}>
              KSh {service.totalPrice.toLocaleString()}
            </span>
          </div>
        ))}
      </div>

      {/* Subtotal */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '8px 0',
        borderTop: `1px solid ${colors.border}`,
        borderBottom: `1px solid ${colors.border}`
      }}>
        <span style={{
          fontSize: '14px',
          fontWeight: '500',
          color: colors.textHeading,
          fontFamily: '"Montserrat", sans-serif'
        }}>
          Subtotal
        </span>
        <span style={{
          fontSize: '14px',
          fontWeight: '600',
          color: colors.textHeading,
          fontFamily: '"Montserrat", sans-serif'
        }}>
          KSh {bookingData.subtotal.toLocaleString()}
        </span>
      </div>

      {/* Travel Fee */}
      {bookingData.isHouseCall && bookingData.travelFee > 0 && (
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '8px 0',
          borderBottom: `1px solid ${colors.border}`
        }}>
          <div>
            <span style={{
              fontSize: '14px',
              fontWeight: '500',
              color: colors.textHeading,
              fontFamily: '"Montserrat", sans-serif'
            }}>
              Travel Fee
            </span>
            <div style={{
              fontSize: '12px',
              color: colors.textBody,
              fontFamily: '"Lora", serif'
            }}>
              {bookingData.distance.toFixed(1)}km √ó KSh 15/km + KSh 100 base
            </div>
          </div>
          <span style={{
            fontSize: '14px',
            fontWeight: '600',
            color: colors.primary,
            fontFamily: '"Montserrat", sans-serif'
          }}>
            KSh {bookingData.travelFee.toLocaleString()}
          </span>
        </div>
      )}

      {/* Total */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '12px 0 0 0',
        marginTop: '8px'
      }}>
        <span style={{
          fontSize: '16px',
          fontWeight: '700',
          color: colors.textHeading,
          fontFamily: '"Montserrat", sans-serif'
        }}>
          Total
        </span>
        <span style={{
          fontSize: '18px',
          fontWeight: '700',
          color: colors.primary,
          fontFamily: '"Montserrat", sans-serif'
        }}>
          KSh {bookingData.total.toLocaleString()}
        </span>
      </div>

      {/* House Call Info */}
      {bookingData.isHouseCall && (
        <div style={{
          marginTop: '12px',
          padding: '12px',
          backgroundColor: colors.primary + '10',
          borderRadius: '8px',
          border: `1px solid ${colors.primary}30`
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            marginBottom: '4px'
          }}>
            <span style={{ fontSize: '16px' }}>üè†</span>
            <span style={{
              fontSize: '14px',
              fontWeight: '600',
              color: colors.primary,
              fontFamily: '"Montserrat", sans-serif'
            }}>
              House Call Service
            </span>
          </div>
          <p style={{
            margin: '0',
            fontSize: '12px',
            color: colors.textBody,
            fontFamily: '"Lora", serif'
          }}>
            Barber will come to: {bookingData.customerAddress || 'Address not specified'}
          </p>
          {bookingData.distance > 0 && (
            <p style={{
              margin: '4px 0 0 0',
              fontSize: '12px',
              color: colors.textBody,
              fontFamily: '"Lora", serif'
            }}>
              Distance: {bookingData.distance.toFixed(1)}km
            </p>
          )}
        </div>
      )}
    </div>
  );
}
