import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { clsx } from 'clsx';
import {
  LayoutDashboard,
  Package,
  Truck,
  MapPin,
  BarChart3,
  Users,
  FileText,
  Settings,
  UserPlus,
  ArrowRightFromLine,
} from 'lucide-react';
import { useApp } from '../context/AppContext';

const navItems = [
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard', adminOnly: false },
  { icon: Package, label: 'Source Records', path: '/source-records', adminOnly: false },
  { icon: Truck, label: 'Transport Log', path: '/transport-log', adminOnly: false },
  { icon: MapPin, label: 'Destination Records', path: '/destination-records', adminOnly: false },
  { icon: BarChart3, label: 'Profit Reports', path: '/profit-reports', adminOnly: true },
  { icon: Users, label: 'Buyers', path: '/buyers', adminOnly: true },
  { icon: UserPlus, label: 'Drivers', path: '/drivers', adminOnly: true },
];

export const Sidebar: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, isDark, setIsDark } = useApp();

  const sidebarClasses = clsx(
    'w-64 bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700 px-6 py-4 fixed left-0 top-0 h-full transition-transform duration-300 ease-in-out lg:relative lg:translate-x-0',
    '[&.mobile-closed]:transform [&.mobile-closed]:-translate-x-full lg:[&.mobile-closed]:translate-x-0',
    !user && 'translate-x-full lg:translate-x-0'
  );

  return (
    <aside className={sidebarClasses}>
      <div className="mb-8">
        <img src="/vite.svg" alt="Logo" className="h-8 w-8 mx-auto mb-2" />
        <h1 className="text-xl font-bold text-gray-900 dark:text-gray-100 text-center">
          Mwalimu Transporters
        </h1>
      </div>

      <nav className="space-y-2">
        {navItems.map(({ icon: Icon, label, path, adminOnly }) => {
          if (adminOnly && user?.role !== 'admin') return null;
          
          const isActive = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={clsx(
                'flex items-center space-x-3 w-full px-3 py-2 rounded-lg text-left transition-colors',
                isActive
                  ? 'bg-bright-green text-white'
                  : 'text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700'
              )}
            >
              <Icon className="h-5 w-5" />
              <span className="font-medium">{label}</span>
            </button>
          );
        })}
      </nav>

      <div className="mt-auto space-y-2">
        <button
          onClick={() => setIsDark(!isDark)}
          className="flex items-center space-x-3 w-full px-3 py-2 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
        >
          <Settings className="h-5 w-5" />
          <span className="font-medium">Settings</span>
        </button>
        
        {user && (
          <button
            onClick={() => navigate('/login')}
            className="flex items-center space-x-3 w-full px-3 py-2 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700"
          >
            <ArrowRightFromLine className="h-5 w-5" />
            <span className="font-medium">Logout</span>
          </button>
        )}
      </div>
    </aside>
  );
};
