import React from "react";
import { useParams, Link } from "react-router-dom";

export default function BarberProfile() {
  const { id } = useParams();

  return (
    <div className="p-6 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-2">Barber Profile</h2>
      <p className="text-gray-700 mb-2">Barber ID: {id}</p>
      <p className="mb-2">Specialties: Fade, Beard Trim, Styling</p>
      <p className="mb-2">Rating: ⭐ 4.8</p>

      <Link to={`/schedule/${id}`}>
        <button className="bg-green-500 text-white px-4 py-2 rounded mt-3">
          Book Appointment
        </button>
      </Link>
    </div>
  );
}
