import React from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import { RootState } from "../store";

export default function BarberSelection() {
  const barbers = useSelector((state: RootState) => state.barbers);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-semibold mb-4">Find Your Trimly Barber</h2>
      <div className="grid gap-4">
        {barbers.map((barber: any) => (
          <div key={barber.id} className="border p-4 rounded shadow">
            <h3 className="text-lg font-bold">{barber.name}</h3>
            <p>{barber.specialty}</p>
            <p>‚≠ê {barber.rating}</p>
            <Link to={`/barber/${barber.id}`}>
              <button className="bg-blue-500 text-white px-3 py-1 mt-2 rounded">
                View Profile
              </button>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}
