import React from 'react';

export default function DashboardBarber() {
    const appointments = [
      { id: 1, client: "Alice", time: "10:00 AM", service: "Haircut" },
      { id: 2, client: "Bob", time: "12:00 PM", service: "Beard Trim" },
    ];
  
    return (
      <div className="p-6">
        <h2 className="text-3xl font-bold mb-6">Barber Dashboard</h2>
  
        <section className="mb-6">
          <h3 className="text-xl font-semibold mb-3">Today’s Appointments</h3>
          <ul className="space-y-3">
            {appointments.map((a) => (
              <li key={a.id} className="border rounded p-3 shadow">
                <p>
                  <strong>{a.client}</strong> - {a.service}
                </p>
                <p className="text-gray-600">{a.time}</p>
              </li>
            ))}
          </ul>
        </section>
  
        <button className="bg-green-500 text-white px-3 py-1 rounded">
          Update Schedule
        </button>
      </div>
    );
  }
