import { getCurrentLocation, geocodeAddress } from '../utils/geolocation';
import BarberMap from '../components/BarberMap';

export default function HouseCalls() {
  const { isDarkMode, colors } = useTheme();
  const { updateBookingDetails, calculateDistance, calculateTravelFee } = useBooking();
  
  const [selectedBarber, setSelectedBarber] = useState<any>(null);
  const [customerAddress, setCustomerAddress] = useState('');
  const [customerCoordinates, setCustomerCoordinates] = useState<{ lat: number; lng: number } | null>(null);
  const [distance, setDistance] = useState<number | null>(null);
  const [travelFee, setTravelFee] = useState<number>(0);
  const [isLoadingLocation, setIsLoadingLocation] = useState(false);
  const [locationError, setLocationError] = useState<string | null>(null);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [selectedService, setSelectedService] = useState('');
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerEmail, setCustomerEmail] = useState('');
  const [specialRequests, setSpecialRequests] = useState('');
  const [showBookingForm, setShowBookingForm] = useState(false);

  // Barbers who accept house calls
  const houseCallBarbers = [
