      shopName: "Elite Barbershop",
      available: true,
      experience: "8 years",
      image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=200&h=200&fit=crop&crop=face",
      price: "KSh 1,500-2,500",
      phone: "+254 700 123 456",
      email: "info@elitebarbershop.com",
      address: "123 Kenyatta Avenue, Nairobi CBD",
      workingHours: "Mon-Sat: 9AM-6PM",
      services: [
        { name: "Classic Haircut", price: 1500, duration: "30 min", description: "Professional haircut with wash and style" },
        { name: "Beard Trim", price: 800, duration: "20 min", description: "Precision beard trimming and styling" },
        { name: "Hot Towel Shave", price: 2000, duration: "45 min", description: "Traditional hot towel shave experience" }
      ],
      shopImage: "https://images.unsplash.com/photo-1560066984-138dadb4c035?w=400&h=250&fit=crop",
      description: "Premium barbershop offering classic cuts and modern styling in the heart of Nairobi CBD.",
      acceptsHouseCalls: true,
      coordinates: { lat: -1.2921, lng: 36.8219 },
      houseCallRates: {
        baseFee: 500,
        perKm: 100,
        minDistance: 5,
        maxDistance: 50
      }
    },
    { 
      id: 3, 
      name: "Sarah Wilson", 
      specialty: "Hair Styling", 
      rating: 4.9, 
      location: "Karen", 
      county: "Nairobi",
      shopName: "Karen Hair Studio",
      available: false,
      experience: "10 years",
      image: "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=200&h=200&fit=crop&crop=face",
      price: "KSh 2,000-3,500",
      phone: "+254 700 345 678",
      email: "appointments@karenhairstudio.com",
      address: "789 Karen Road, Karen",
      workingHours: "Tue-Sat: 9AM-5PM",
      services: [
        { name: "Hair Styling", price: 2000, duration: "45 min", description: "Professional hair styling and blow-dry" },
        { name: "Hair Coloring", price: 3500, duration: "90 min", description: "Professional hair coloring services" },
        { name: "Hair Treatment", price: 2500, duration: "60 min", description: "Deep conditioning and hair treatment" }
      ],
      shopImage: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?w=400&h=250&fit=crop",
      description: "Luxury hair studio offering premium hair services in an elegant Karen setting.",
      acceptsHouseCalls: true,
      coordinates: { lat: -1.3197, lng: 36.8140 },
      houseCallRates: {
        baseFee: 800,
        perKm: 150,
        minDistance: 3,
        maxDistance: 40
      }
    },
    { 
      id: 5, 
      name: "James Mwangi", 
      specialty: "Traditional Cuts", 
      rating: 4.5, 
      location: "Thika", 
      county: "Kiambu",
      shopName: "Thika Classic Barbers",
      available: true,
      experience: "7 years",
      image: "https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?w=200&h=200&fit=crop&crop=face",
      price: "KSh 600-1,200",
      phone: "+254 700 567 890",
      email: "info@thikaclassic.com",
      address: "654 Thika Road, Thika",
      workingHours: "Mon-Sat: 7AM-7PM",
      services: [
        { name: "Haircut", price: 600, duration: "30 min", description: "Professional haircut with wash and style" },
        { name: "Beard Trim", price: 500, duration: "20 min", description: "Precision beard trimming and styling" },
        { name: "Hair Wash", price: 300, duration: "15 min", description: "Professional hair wash and conditioning" }
      ],
      shopImage: "https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400&h=250&fit=crop",
      description: "Affordable traditional barbershop serving the Thika community with quality cuts.",
      acceptsHouseCalls: true,
      coordinates: { lat: -1.0470, lng: 37.0744 },
      houseCallRates: {
        baseFee: 300,
        perKm: 80,
        minDistance: 2,
        maxDistance: 60
      }
    },
    { 
      id: 6, 
      name: "Grace Wanjiku", 
      specialty: "Ladies Hair", 
      rating: 4.8, 
      location: "Runda", 
      county: "Nairobi",
      shopName: "Runda Hair Salon",
      available: true,
      experience: "9 years",
      image: "https://images.unsplash.com/photo-1580618672591-eb180b1a973f?w=200&h=200&fit=crop&crop=face",
      price: "KSh 1,800-3,000",
      phone: "+254 700 678 901",
      email: "bookings@rundahairsalon.com",
      address: "987 Runda Road, Runda",
      workingHours: "Mon-Sat: 9AM-6PM",
      services: [
        { name: "Hair Styling", price: 1800, duration: "45 min", description: "Professional hair styling and blow-dry" },
        { name: "Hair Coloring", price: 3000, duration: "90 min", description: "Professional hair coloring services" },
        { name: "Hair Treatment", price: 2200, duration: "60 min", description: "Deep conditioning and hair treatment" },
        { name: "Scalp Massage", price: 1500, duration: "30 min", description: "Relaxing scalp massage and treatment" }
      ],
      shopImage: "https://images.unsplash.com/photo-1560066984-138dadb4c035?w=400&h=250&fit=crop",
      description: "Exclusive hair salon offering premium hair services for women in Runda.",
      acceptsHouseCalls: true,
      coordinates: { lat: -1.2000, lng: 36.8000 },
      houseCallRates: {
        baseFee: 600,
        perKm: 120,
        minDistance: 4,
        maxDistance: 45
      }
    }
  ];

  const timeSlots = [
    "9:00 AM", "9:30 AM", "10:00 AM", "10:30 AM", "11:00 AM", "11:30 AM",
    "12:00 PM", "12:30 PM", "1:00 PM", "1:30 PM", "2:00 PM", "2:30 PM",
    "3:00 PM", "3:30 PM", "4:00 PM", "4:30 PM", "5:00 PM", "5:30 PM"
  ];

  useEffect(() => {
    if (customerCoordinates && selectedBarber?.coordinates) {
      const calculatedDistance = calculateDistance(
        customerCoordinates.lat,
        customerCoordinates.lng,
        selectedBarber.coordinates.lat,
        selectedBarber.coordinates.lng
      );
      setDistance(calculatedDistance);
      
      const fee = calculateTravelFee(calculatedDistance);
      setTravelFee(fee);
    }
  }, [customerCoordinates, selectedBarber, calculateDistance, calculateTravelFee]);

  const handleUseCurrentLocation = async () => {
    setIsLoadingLocation(true);
    setLocationError(null);
    
    try {
      const result = await getCurrentLocation();
      if (result.coordinates) {
        setCustomerCoordinates(result.coordinates);
        setCustomerAddress('Current Location');
        setLocationError(null);
      } else {
        setLocationError(result.error || 'Unable to get your location');
      }
    } catch (error) {
      setLocationError('Error getting your location');
    } finally {
      setIsLoadingLocation(false);
    }
  };

  const handleAddressGeocode = async () => {
    if (!customerAddress.trim()) return;
    
    setIsLoadingLocation(true);
    setLocationError(null);
    
    try {
      const result = await geocodeAddress(customerAddress);
      if (result.coordinates) {
        setCustomerCoordinates(result.coordinates);
        setLocationError(null);
      } else {
        setLocationError(result.error || 'Address not found');
      }
    } catch (error) {
      setLocationError('Error geocoding address');
    } finally {
      setIsLoadingLocation(false);
    }
  };

  const handleBarberSelect = (barber: any) => {
    setSelectedBarber(barber);
    setShowBookingForm(true);
  };

  const handleBooking = () => {
    if (!selectedBarber || !selectedDate || !selectedTime || !selectedService || !customerName || !customerPhone) {
      alert('Please fill in all required fields');
      return;
    }
    
    const service = selectedBarber.services.find((s: any) => s.name === selectedService);
    
    updateBookingDetails({
      barber: selectedBarber,
      selectedServices: [{
        id: selectedService,
        name: selectedService,
        price: service?.price || 0,
        duration: service?.duration || '',
        category: 'Hair Services',
        image: 'https://images.unsplash.com/photo-1560066984-138dadb4c035?w=200&h=200&fit=crop',
        description: service?.description || '',
        quantity: 1,
        totalPrice: service?.price || 0
      }],
      date: selectedDate,
      time: selectedTime,
      customerName,
      customerPhone,
      customerEmail,
      notes: specialRequests,
      isHouseCall: true,
      customerCoordinates,
      customerAddress,
      travelFee,
      distance
    });
    
    // Store booking data in localStorage for payment page
    const bookingData = {
      barberId: selectedBarber.id,
      barberName: selectedBarber.name,
      barberShop: selectedBarber.shopName,
      service: selectedService,
      date: selectedDate,
      time: selectedTime,
      customerName,
      customerPhone,
      customerEmail,
      specialRequests,
      price: service?.price || 0,
      isHouseCall: true,
      travelFee,
      distance,
      customerAddress
    };
    
    localStorage.setItem('bookingData', JSON.stringify(bookingData));
  };

  return (
    <div style={{ 
