              type="button"
              onClick={handleUseCurrentLocation}
              disabled={isLoadingLocation}
              style={{
                padding: '12px 20px',
                backgroundColor: colors.secondary,
                color: 'white',
                border: 'none',
                borderRadius: '10px',
                cursor: isLoadingLocation ? 'not-allowed' : 'pointer',
                fontSize: '16px',
                fontWeight: '600',
                opacity: isLoadingLocation ? 0.6 : 1,
                fontFamily: '"Montserrat", sans-serif',
                marginRight: '12px',
