              <div 
                key={barber.id}
                onClick={() => handleBarberSelect(barber)}
                style={{
                  backgroundColor: colors.cardBackground,
                  padding: '20px',
                  borderRadius: '12px',
                  border: `2px solid ${selectedBarber?.id === barber.id ? colors.primary : colors.border}`,
