import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';

interface User {
  id: string;
  email: string;
  password: string;
  role: 'customer' | 'barber' | 'admin';
  name: string;
  isApproved: boolean;
}

export default function Login() {
  const { colors } = useTheme();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Mock users database
  const users: User[] = [
    {
      id: '1',
      email: 'admin@trimly.com',
      password: 'admin123',
      role: 'admin',
      name: 'Admin User',
      isApproved: true
    },
    {
      id: '2',
      email: 'barber1@trimly.com',
      password: 'barber123',
      role: 'barber',
      name: 'John Smith',
      isApproved: true
    },
    {
      id: '3',
      email: 'barber2@trimly.com',
      password: 'barber123',
      role: 'barber',
      name: 'Mike Jones',
      isApproved: false // Not approved by admin
    },
    {
      id: '4',
      email: 'customer@trimly.com',
      password: 'customer123',
      role: 'customer',
      name: 'Customer User',
      isApproved: true
    }
  ];

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    const user = users.find(u => u.email === email && u.password === password);

    if (!user) {
      setError('Invalid email or password');
      setIsLoading(false);
      return;
    }

    if (user.role === 'barber' && !user.isApproved) {
      setError('Your barber account is pending admin approval');
      setIsLoading(false);
      return;
    }

    // Store user in localStorage
    localStorage.setItem('user', JSON.stringify({
      id: user.id,
      email: user.email,
      role: user.role,
      name: user.name,
      isApproved: user.isApproved
    }));

    // Redirect based on role
    switch (user.role) {
      case 'admin':
        navigate('/admin');
        break;
      case 'barber':
        navigate('/barber');
        break;
      case 'customer':
        navigate('/');
        break;
      default:
        navigate('/');
    }

    setIsLoading(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      backgroundImage: 'linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url("https://images.unsplash.com/photo-1560066984-138dadb4c035?w=1920&h=1080&fit=crop")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundAttachment: 'fixed',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      fontFamily: '"Lora", serif'
    }}>
      <div style={{
        backgroundColor: colors.surface,
        padding: '40px',
        borderRadius: '20px',
        boxShadow: '0 20px 40px rgba(0,0,0,0.3)',
        width: '100%',
        maxWidth: '400px',
        border: `1px solid ${colors.border}`
      }}>
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h1 style={{
            fontSize: '2rem',
            fontWeight: '700',
            color: colors.textHeading,
            marginBottom: '8px',
            fontFamily: '"Montserrat", sans-serif'
          }}>
            Welcome Back
          </h1>
          <p style={{
            color: colors.textBody,
            fontSize: '1rem',
            fontFamily: '"Lora", serif'
          }}>
            Sign in to your Trimly account
          </p>
        </div>

        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              fontWeight: '600',
              color: colors.textHeading,
              fontSize: '14px',
              fontFamily: '"Montserrat", sans-serif'
            }}>
              Email Address
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
              style={{
                width: '100%',
                padding: '12px 16px',
                border: `1px solid ${colors.border}`,
                borderRadius: '8px',
                fontSize: '16px',
                backgroundColor: colors.inputBackground,
                color: colors.textHeading,
                fontFamily: '"Lora", serif',
                outline: 'none',
                transition: 'border-color 0.2s ease'
              }}
              onFocus={(e) => e.target.style.borderColor = colors.primary}
              onBlur={(e) => e.target.style.borderColor = colors.border}
            />
          </div>

          <div style={{ marginBottom: '24px' }}>
            <label style={{
              display: 'block',
              marginBottom: '8px',
              fontWeight: '600',
              color: colors.textHeading,
              fontSize: '14px',
              fontFamily: '"Montserrat", sans-serif'
            }}>
              Password
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
              style={{
                width: '100%',
                padding: '12px 16px',
                border: `1px solid ${colors.border}`,
                borderRadius: '8px',
                fontSize: '16px',
                backgroundColor: colors.inputBackground,
                color: colors.textHeading,
                fontFamily: '"Lora", serif',
                outline: 'none',
                transition: 'border-color 0.2s ease'
              }}
              onFocus={(e) => e.target.style.borderColor = colors.primary}
              onBlur={(e) => e.target.style.borderColor = colors.border}
            />
          </div>

          {error && (
            <div style={{
              backgroundColor: colors.error + '20',
              color: colors.error,
              padding: '12px',
              borderRadius: '8px',
              marginBottom: '20px',
              fontSize: '14px',
              fontFamily: '"Lora", serif',
              border: `1px solid ${colors.error}40`
            }}>
              {error}
            </div>
          )}

          <button
            type="submit"
            disabled={isLoading}
            style={{
              width: '100%',
              padding: '14px',
              backgroundColor: colors.primary,
              color: '#FFFFFF',
              border: 'none',
              borderRadius: '8px',
              fontSize: '16px',
              fontWeight: '600',
              fontFamily: '"Montserrat", sans-serif',
              cursor: isLoading ? 'not-allowed' : 'pointer',
              opacity: isLoading ? 0.7 : 1,
              transition: 'all 0.2s ease',
              boxShadow: '0 4px 12px rgba(23, 165, 137, 0.3)'
            }}
            onMouseEnter={(e) => {
              if (!isLoading) {
                e.currentTarget.style.backgroundColor = colors.secondary;
                e.currentTarget.style.transform = 'translateY(-1px)';
                e.currentTarget.style.boxShadow = '0 6px 16px rgba(23, 165, 137, 0.4)';
              }
            }}
            onMouseLeave={(e) => {
              if (!isLoading) {
                e.currentTarget.style.backgroundColor = colors.primary;
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(23, 165, 137, 0.3)';
              }
            }}
          >
            {isLoading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <div style={{
          marginTop: '30px',
          padding: '20px',
          backgroundColor: colors.background,
          borderRadius: '8px',
          border: `1px solid ${colors.border}`
        }}>
          <h3 style={{
            fontSize: '14px',
            fontWeight: '600',
            color: colors.textHeading,
            marginBottom: '12px',
            fontFamily: '"Montserrat", sans-serif'
          }}>
            Demo Accounts:
          </h3>
          <div style={{ fontSize: '12px', color: colors.textBody, lineHeight: '1.6' }}>
            <div><strong>Admin:</strong> admin@trimly.com / admin123</div>
            <div><strong>Barber:</strong> barber1@trimly.com / barber123</div>
            <div><strong>Customer:</strong> customer@trimly.com / customer123</div>
          </div>
        </div>

        <div style={{
          textAlign: 'center',
          marginTop: '20px'
        }}>
          <button
            onClick={() => navigate('/')}
            style={{
              color: colors.primary,
              backgroundColor: 'transparent',
              border: 'none',
              fontSize: '14px',
              cursor: 'pointer',
              fontFamily: '"Lora", serif',
              textDecoration: 'underline'
            }}
          >
            ‚Üê Back to Home
          </button>
        </div>
      </div>
    </div>
  );
}
