import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useApp } from '../../context/AppContext';

export const LoginPage: React.FC = () => {
  const [role, setRole] = useState<'admin' | 'user'>('user');
  const navigate = useNavigate();
  const { setUser, setIsAuthenticated } = useApp();

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    
    // Mock login - create fake user
    const user = {
      id: '1',
      name: role === 'admin' ? 'Admin User' : 'Regular User',
      email: role === 'admin' ? 'admin@mwalimu.com' : 'user@mwalimu.com',
      role,
    };
    
    setUser(user);
    setIsAuthenticated(true);
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <div className="mx-auto h-12 w-12 bg-bright-green rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-xl">MT</span>
          </div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900 dark:text-gray-100">
            Mwalimu Transporters
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600 dark:text-gray-400">
            Miraa Transport Management System
          </p>
        </div>
        
        <form className="mt-8 space-y-6" onSubmit={handleLogin}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="role" className="sr-only">
                Select Role
              </label>
              <select
                id="role"
                name="role"
                value={role}
                onChange={(e) => setRole(e.target.value as 'admin' | 'user')}
                className="relative block w-full px-3 py-2 border border-gray-300 dark:border-gray-600 placeholder-gray-500 dark:placeholder-gray-400 text-gray-900 dark:text-gray-100 rounded-t-md focus:outline-none focus:ring-bright-green focus:border-bright-green focus:z-10 sm:text-sm bg-white dark:bg-gray-700"
                required
              >
                <option value="user">User - Record Daily Activities</option>
                <option value="admin">Admin - Manage All Records</option>
              </select>
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-bright-green hover:bg-miraa-green focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-bright-green transition-colors duration-200"
            >
              Sign In
            </button>
          </div>
          
          <div className="text-center">
            <p className="text-sm text-gray-600 dark:text-gray-400">
              Demo: Select your role and click "Sign In"
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};
