import React from "react";
import { useLocation } from "react-router-dom";
import { useState } from "react";

export default function PaymentPage() {
  const location = useLocation();
  const bookingDetails = location.state as { barberId: string; date: string; time: string };

  const [cardNumber, setCardNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");

  const handlePayment = () => {
    alert(`Payment processed for appointment with Barber ${bookingDetails.barberId} on ${bookingDetails.date} at ${bookingDetails.time}`);
  };

  return (
    <div className="p-6 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold mb-4">Complete Your Payment</h2>

      <p className="mb-2">Barber ID: {bookingDetails?.barberId}</p>
      <p className="mb-2">Date: {bookingDetails?.date}</p>
      <p className="mb-4">Time: {bookingDetails?.time}</p>

      <input
        type="text"
        placeholder="Card Number"
        value={cardNumber}
        onChange={(e) => setCardNumber(e.target.value)}
        className="border p-2 w-full mb-3"
      />
      <input
        type="text"
        placeholder="MM/YY"
        value={expiry}
        onChange={(e) => setExpiry(e.target.value)}
        className="border p-2 w-full mb-3"
      />
      <input
        type="text"
        placeholder="CVV"
        value={cvv}
        onChange={(e) => setCvv(e.target.value)}
        className="border p-2 w-full mb-3"
      />

      <button
        onClick={handlePayment}
        className="bg-black text-white px-6 py-2 rounded"
      >
        Pay Now
      </button>
    </div>
  );
}
