import React, { useState, useMemo } from 'react';
import { Card } from '../../components/Card';
import { Modal } from '../../components/Modal';
import { FormInput } from '../../components/FormInput';
import { FormSelect } from '../../components/FormSelect';
import { useApp } from '../../context/AppContext';
import { useNotificationService } from '../../services/notificationService';
import { 
  Settings, 
  Save, 
  RefreshCw, 
  Bell, 
  Shield, 
  Palette, 
  Database,
  Lock,
  CheckCircle,
  XCircle
} from 'lucide-react';

interface SystemSetting {
  id: string;
  category: 'general' | 'security' | 'notifications' | 'appearance' | 'system';
  key: string;
  label: string;
  description: string;
  type: 'boolean' | 'string' | 'number' | 'select' | 'password';
  value: any;
  options?: { value: string; label: string }[];
  required: boolean;
  sensitive: boolean;
  lastModified?: string;
  modifiedBy?: string;
}

export const SettingsCardPage: React.FC = () => {
  const { user, isDark, setIsDark } = useApp();
  const { showSuccessNotification, showErrorNotification } = useNotificationService();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingSetting, setEditingSetting] = useState<SystemSetting | null>(null);
  const [activeCategory, setActiveCategory] = useState<string>('general');
  
  // Mock system settings data
  const [systemSettings, setSystemSettings] = useState<SystemSetting[]>([
    // General Settings
    {
      id: '1',
      category: 'general',
      key: 'app_name',
      label: 'Application Name',
      description: 'The name displayed in the application header',
      type: 'string',
      value: 'Mwalimu Transporters',
      required: true,
      sensitive: false,
      lastModified: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
      modifiedBy: 'admin',
    },
    {
      id: '2',
      category: 'general',
      key: 'timezone',
      label: 'Default Timezone',
      description: 'The default timezone for the application',
      type: 'select',
      value: 'Africa/Nairobi',
      options: [
        { value: 'Africa/Nairobi', label: 'Nairobi (EAT)' },
        { value: 'Africa/Dar_es_Salaam', label: 'Dar es Salaam (EAT)' },
        { value: 'Africa/Kampala', label: 'Kampala (EAT)' },
        { value: 'UTC', label: 'UTC' },
      ],
      required: true,
      sensitive: false,
    },
    {
      id: '3',
      category: 'general',
      key: 'language',
      label: 'Default Language',
      description: 'The default language for the application interface',
      type: 'select',
      value: 'en',
      options: [
        { value: 'en', label: 'English' },
        { value: 'sw', label: 'Swahili' },
        { value: 'fr', label: 'French' },
      ],
      required: true,
      sensitive: false,
    },
    
    // Security Settings
    {
      id: '4',
      category: 'security',
      key: 'password_min_length',
      label: 'Minimum Password Length',
      description: 'Minimum number of characters required for passwords',
      type: 'number',
      value: 8,
      required: true,
      sensitive: false,
    },
    {
      id: '5',
      category: 'security',
      key: 'session_timeout',
      label: 'Session Timeout (minutes)',
      description: 'How long a user session remains active before requiring re-authentication',
      type: 'number',
      value: 30,
      required: true,
      sensitive: false,
    },
    {
      id: '6',
      category: 'security',
      key: 'two_factor_required',
      label: 'Require Two-Factor Authentication',
      description: 'Force all users to enable two-factor authentication',
      type: 'boolean',
      value: false,
      required: false,
      sensitive: false,
    },
    {
      id: '7',
      category: 'security',
      key: 'api_key',
      label: 'API Secret Key',
      description: 'Secret key used for API authentication',
      type: 'password',
      value: '••••••••••••••••',
      required: true,
      sensitive: true,
    },
    
    // Notification Settings
    {
      id: '8',
      category: 'notifications',
      key: 'email_notifications',
      label: 'Email Notifications',
      description: 'Enable email notifications for system events',
      type: 'boolean',
      value: true,
      required: false,
      sensitive: false,
    },
    {
      id: '9',
      category: 'notifications',
      key: 'push_notifications',
      label: 'Push Notifications',
      description: 'Enable push notifications for mobile devices',
      type: 'boolean',
      value: true,
      required: false,
      sensitive: false,
    },
    {
      id: '10',
      category: 'notifications',
      key: 'notification_sound',
      label: 'Notification Sound',
      description: 'Play sound when notifications are received',
      type: 'boolean',
      value: false,
      required: false,
      sensitive: false,
    },
    
    // Appearance Settings
    {
      id: '11',
      category: 'appearance',
      key: 'theme',
      label: 'Default Theme',
      description: 'The default theme for new users',
      type: 'select',
      value: 'light',
      options: [
        { value: 'light', label: 'Light Theme' },
        { value: 'dark', label: 'Dark Theme' },
        { value: 'auto', label: 'Auto (System)' },
      ],
      required: true,
      sensitive: false,
    },
    {
      id: '12',
      category: 'appearance',
      key: 'primary_color',
      label: 'Primary Color',
      description: 'The primary color used throughout the application',
      type: 'select',
      value: 'green',
      options: [
        { value: 'green', label: 'Green' },
        { value: 'blue', label: 'Blue' },
        { value: 'purple', label: 'Purple' },
        { value: 'red', label: 'Red' },
      ],
      required: true,
      sensitive: false,
    },
    
    // System Settings
    {
      id: '13',
      category: 'system',
      key: 'maintenance_mode',
      label: 'Maintenance Mode',
      description: 'Enable maintenance mode to restrict access to the system',
      type: 'boolean',
      value: false,
      required: false,
      sensitive: false,
    },
    {
      id: '14',
      category: 'system',
      key: 'auto_backup',
      label: 'Automatic Backup',
      description: 'Enable automatic daily backups of system data',
      type: 'boolean',
      value: true,
      required: false,
      sensitive: false,
    },
    {
      id: '15',
      category: 'system',
      key: 'log_level',
      label: 'Log Level',
      description: 'The level of detail in system logs',
      type: 'select',
      value: 'info',
      options: [
        { value: 'debug', label: 'Debug' },
        { value: 'info', label: 'Info' },
        { value: 'warn', label: 'Warning' },
        { value: 'error', label: 'Error' },
      ],
      required: true,
      sensitive: false,
    },
  ]);

  const [formData, setFormData] = useState<Partial<SystemSetting>>({});

  const categories = [
    { key: 'general', label: 'General', icon: Settings, color: 'blue' },
    { key: 'security', label: 'Security', icon: Shield, color: 'red' },
    { key: 'notifications', label: 'Notifications', icon: Bell, color: 'yellow' },
    { key: 'appearance', label: 'Appearance', icon: Palette, color: 'purple' },
    { key: 'system', label: 'System', icon: Database, color: 'green' },
  ];

  const filteredSettings = useMemo(() => {
    return systemSettings.filter(setting => setting.category === activeCategory);
  }, [systemSettings, activeCategory]);

  const handleOpenModal = (setting?: SystemSetting) => {
    if (setting) {
      setEditingSetting(setting);
      setFormData(setting);
    } else {
      setEditingSetting(null);
      setFormData({});
    }
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setEditingSetting(null);
    setFormData({});
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (editingSetting) {
      setSystemSettings(
        systemSettings.map((setting) =>
          setting.id === editingSetting.id 
            ? { 
                ...setting, 
                value: formData.value,
                lastModified: new Date().toISOString(),
                modifiedBy: user?.name || 'admin'
              } 
            : setting
        )
      );
      showSuccessNotification('Setting updated successfully!');
    }

    handleCloseModal();
  };

  const handleResetToDefault = (setting: SystemSetting) => {
    if (window.confirm(`Reset "${setting.label}" to default value?`)) {
      // In a real app, you would reset to the actual default value
      showSuccessNotification(`"${setting.label}" reset to default value`);
    }
  };

  const handleBulkSave = () => {
    showSuccessNotification('All settings saved successfully!');
  };

  const handleExportSettings = () => {
    const settingsData = JSON.stringify(systemSettings, null, 2);
    const blob = new Blob([settingsData], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'system-settings.json';
    a.click();
    URL.revokeObjectURL(url);
    showSuccessNotification('Settings exported successfully!');
  };

  const handleImportSettings = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (e) => {
          try {
            const importedSettings = JSON.parse(e.target?.result as string);
            setSystemSettings(importedSettings);
            showSuccessNotification('Settings imported successfully!');
          } catch (error) {
            showErrorNotification('Invalid settings file format');
          }
        };
        reader.readAsText(file);
      }
    };
    input.click();
  };

  const renderSettingValue = (setting: SystemSetting) => {
    switch (setting.type) {
      case 'boolean':
        return (
          <div className="flex items-center">
            {setting.value ? (
              <CheckCircle className="h-4 w-4 text-green-500" />
            ) : (
              <XCircle className="h-4 w-4 text-red-500" />
            )}
            <span className="ml-2 text-sm text-gray-600 dark:text-gray-400">
              {setting.value ? 'Enabled' : 'Disabled'}
            </span>
          </div>
        );
      case 'password':
        return (
          <div className="flex items-center">
            <Lock className="h-4 w-4 text-gray-400" />
            <span className="ml-2 text-sm text-gray-600 dark:text-gray-400">
              {setting.value}
            </span>
          </div>
        );
      case 'select':
        const option = setting.options?.find(opt => opt.value === setting.value);
        return (
          <span className="text-sm text-gray-600 dark:text-gray-400">
            {option?.label || setting.value}
          </span>
        );
      default:
        return (
          <span className="text-sm text-gray-600 dark:text-gray-400">
            {setting.value}
          </span>
        );
    }
  };

  return (
    <div className="space-y-6 max-w-full overflow-hidden">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100">Settings Card</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            System configuration and application settings management
          </p>
        </div>
        
        <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-3">
          <button 
            onClick={handleImportSettings}
            className="flex items-center space-x-2 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
          >
            <RefreshCw className="h-4 w-4" />
            <span>Import</span>
          </button>
          <button 
            onClick={handleExportSettings}
            className="flex items-center space-x-2 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg hover:bg-gray-50 dark:hover:bg-gray-700"
          >
            <Database className="h-4 w-4" />
            <span>Export</span>
          </button>
          <button
            onClick={handleBulkSave}
            className="flex items-center space-x-2 px-4 py-2 bg-bright-green text-white rounded-lg hover:bg-green-600 transition-colors"
          >
            <Save className="h-4 w-4" />
            <span>Save All</span>
          </button>
        </div>
      </div>

      {/* Category Tabs */}
      <Card className="p-4">
        <div className="flex flex-wrap gap-2">
          {categories.map((category) => {
            const Icon = category.icon;
            const isActive = activeCategory === category.key;
            return (
              <button
                key={category.key}
                onClick={() => setActiveCategory(category.key)}
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                  isActive
                    ? `bg-${category.color}-100 dark:bg-${category.color}-900/20 text-${category.color}-800 dark:text-${category.color}-200 border border-${category.color}-200 dark:border-${category.color}-700`
                    : 'text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700'
                }`}
              >
                <Icon className="h-4 w-4" />
                <span className="font-medium">{category.label}</span>
              </button>
            );
          })}
        </div>
      </Card>

      {/* Settings List */}
      <div className="space-y-4">
        {filteredSettings.map((setting) => (
          <Card key={setting.id} className="p-6">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <div className="flex items-center space-x-3 mb-2">
                  <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100">
                    {setting.label}
                  </h3>
                  {setting.required && (
                    <span className="text-xs bg-red-100 dark:bg-red-900/20 text-red-800 dark:text-red-200 px-2 py-1 rounded-full">
                      Required
                    </span>
                  )}
                  {setting.sensitive && (
                    <span className="text-xs bg-yellow-100 dark:bg-yellow-900/20 text-yellow-800 dark:text-yellow-200 px-2 py-1 rounded-full">
                      Sensitive
                    </span>
                  )}
                </div>
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-3">
                  {setting.description}
                </p>
                <div className="flex items-center space-x-4">
                  <div className="text-sm">
                    <span className="font-medium text-gray-700 dark:text-gray-300">Current Value:</span>
                    <div className="mt-1">
                      {renderSettingValue(setting)}
                    </div>
                  </div>
                  {setting.lastModified && (
                    <div className="text-sm text-gray-500 dark:text-gray-400">
                      <span className="font-medium">Last Modified:</span>
                      <div>{new Date(setting.lastModified).toLocaleDateString()}</div>
                      <div className="text-xs">by {setting.modifiedBy}</div>
                    </div>
                  )}
                </div>
              </div>
              <div className="flex items-center space-x-2 ml-4">
                <button
                  onClick={() => handleOpenModal(setting)}
                  className="p-2 text-blue-600 hover:text-blue-800 hover:bg-blue-50 dark:hover:bg-blue-900/20 rounded-lg transition-colors"
                  title="Edit setting"
                >
                  <Settings className="h-4 w-4" />
                </button>
                <button
                  onClick={() => handleResetToDefault(setting)}
                  className="p-2 text-orange-600 hover:text-orange-800 hover:bg-orange-50 dark:hover:bg-orange-900/20 rounded-lg transition-colors"
                  title="Reset to default"
                >
                  <RefreshCw className="h-4 w-4" />
                </button>
              </div>
            </div>
          </Card>
        ))}
      </div>

      {/* Edit Setting Modal */}
      <Modal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        title={editingSetting ? `Edit ${editingSetting.label}` : 'Add New Setting'}
        size="lg"
      >
        <form onSubmit={handleSubmit} className="space-y-6">
          {editingSetting && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Setting Description
                </label>
                <p className="text-sm text-gray-600 dark:text-gray-400 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  {editingSetting.description}
                </p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                  Current Value
                </label>
                {editingSetting.type === 'boolean' ? (
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      checked={formData.value || false}
                      onChange={(e) => setFormData({ ...formData, value: e.target.checked })}
                      className="h-4 w-4 text-bright-green rounded border-gray-300 focus:ring-bright-green"
                    />
                    <span className="text-sm text-gray-600 dark:text-gray-400">
                      {formData.value ? 'Enabled' : 'Disabled'}
                    </span>
                  </div>
                ) : editingSetting.type === 'select' ? (
                  <FormSelect
                    value={formData.value || ''}
                    onChange={(value) => setFormData({ ...formData, value })}
                    options={editingSetting.options || []}
                  />
                ) : editingSetting.type === 'password' ? (
                  <div className="relative">
                    <FormInput
                      type="password"
                      value={formData.value || ''}
                      onChange={(value) => setFormData({ ...formData, value })}
                      placeholder="Enter new password"
                    />
                  </div>
                ) : (
                  <FormInput
                    type={editingSetting.type === 'number' ? 'number' : 'text'}
                    value={formData.value || ''}
                    onChange={(value) => setFormData({ ...formData, value })}
                  />
                )}
              </div>
            </>
          )}

          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={handleCloseModal}
              className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-bright-green text-white rounded-lg hover:bg-green-600 transition-colors"
            >
              Save Changes
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};
